import React from "react";
import pokeball from "../images/pokeball.png"



export default function Sidebar(){

    const [pokeData, setPokedata] = React.useState([])

    React.useEffect(() => {
        const fetchData = async () => {
          const response = await fetch(`https://pokeapi.co/api/v2/pokemon?limit=20`);
          const data = await response.json();
          setPokedata(data.results);
        };

        fetchData();
      }, []);

      console.log(pokeData);

      const pokemonData = pokeData.map((pokemon, index) => (
        <div key={index} id="pokemon-data-box">
          {pokemon.name}
        </div>
      ));


console.log(pokeData)








    return( 
        <>
<div id="sidebar">
<div id="searchbox-div">
<input type="searchbox" id="searchbox" placeholder="Search"/>
<img src={pokeball} alt="" id="pokeball"/> 
</div>




{pokeData.length > 0 ? pokemonData : <div>Loading...</div>}

</div>
        </>
    )
}
